    useEffect(() => {
        const fetchData = async () => {
        //   dispatch({ type: 'FETCH_REQUEST' });
          try {
            const result = await PlaceKind.get(`/${id}`);
            // dispatch({ type: 'FETCH_SUCCESS', payload: result.data});
          } catch (err) {
            // dispatch({ type: 'FETCH_ERROR', payload: getError(err) });
              console.log(err)
