import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import {HotelsContext } from "../context/HotelsContext";
import HotelFinder from "../apis/HotelFinder";
import StarRating from "../components/StarRating";
import Reviews from "../components/Reviews";
import AddReview from "../components/AddReview";

const HotelDetail = () => {
  const { id } = useParams();
  const { selectedHotel, setSelectedHotel} = useContext(
   HotelsContext
  );

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await HotelFinder.get(`/${id}`);
        console.log(response);
        setSelectedHotel(response.data.data.id);
      } catch (error) {
        console.log(error.message);
      }
    };

    fetchData();
  }, [id, setSelectedHotel]);
  return (
    <div>
      {selectedHotel && (
        <>
          <h1 className="text-center display-1">
            {selectedHotel.hotel.name}
          </h1>
          <div className="text-center">
            <StarRating rating={selectedHotel.hotel.average_rating} />
            <span className="text-warning ml-1">
              {selectedHotel.hotel.count
                ? `(${selectedHotel.hotel.count})`
                : "(0)"}
            </span>
          </div>
          <div className="mt-3">
            <Reviews reviews={selectedHotel.reviews} />
          </div>
          <AddReview />
        </>
      )}
    </div>
  );
};

export default HotelDetail;
