import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import Button from "react-bootstrap/Button";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";
import moment from "moment";


const Create = () => {
    const state = useLocation().state;
    const [value, setValue] = useState(state?.title || "");
    const [title, setTitle] = useState(state?.description || "");
    const [file, setFile] = useState(null);
    const [where_to_stay, setWhereToStay] = useState(state?.where_to_stay || "")

    const navigate = useNavigate();
    
    const upload = async () => {
        try {
          const formData = new FormData();
          formData.append("file", file);
          const res = await axios.post("/upload", formData);
          return res.data;
        } catch (err) {
          console.log(err);
        }
    };
    const handleClick = async (e) => {
        e.preventDefault();
        const imgUrl = await upload();
        try {
            state
              ? await axios.put(`http://localhost:8090/api/v1/places${state.id}`, {
                  title, where_to_stay,
                  description: value,
                // where_to_stay: value,
                  image_url1: file ? imgUrl : "",
                })
                : await axios.post(`http://localhost:8090/api/v1/places`, {
                    title, where_to_stay,
                description: value,
                // where_to_stay: value,
                  image_url1: file ? imgUrl : "",
                  date: moment(Date.now()).format("YYYY-MM-DD HH:mm:ss"),
                });
                navigate("/")
          } catch (err) {
            console.log(err);
          }
        };



  return (
    <div className="create-place">
      <div className="content">
        <input
          type="text"
          placeholder="Title"
          onChange={(e) => setTitle(e.target.value)}
              />
               {/* <input
          type="text"
