import React, { useEffect, useReducer } from "react";
import axios from "axios";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import logger from "use-reducer-logger";
import Place from "../components/Place.jsx";
import { Helmet } from 'react-helmet-async';
import Loader from "../components/Rings.jsx";
import Message from "../components/Message.jsx";



const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, posts: action.payload, loading: false };
    case "FETCH_ERROR":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};


function Home() {
  const [{ loading, error, posts }, dispatch] = useReducer(logger(reducer), {
    posts: [],
    loading: true,
    error: "",
  });

  // const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("/api/v1/posts");
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_ERROR", payload: err.message });
      }

      // setPosts(result.data);
    };
    fetchData();
  }, []);

  return (
    <div className="home">
     <Helmet>
        <title>Posts</title>
      </Helmet>
      <h1 className="content-title display-1 font-weight-light  text-center p-4">        Posts{" "}
          </h1>
        <Container className="posts">
          {loading ? (
            <Loader />
          ) : error ? (
            <Message variant="danger">{error}</Message> 
          ) : (
              <Row className="row">
                {posts.map((post) => (
                  <Row key={post.slug}>
            <Post post={post}></Post>
              </Row>
                  ))}
              </Row>
          )}
        </Container>
    </div>
  );
}

export default Home;
